import{_ as h,c as t,o as i,a2 as c}from"./chunks/framework.nRfFlDZQ.js";const v=JSON.parse('{"title":"Chương 1: Giới thiệu về Convolutional Neural Network và Deep Learning trong Viễn thám","description":"","frontmatter":{},"headers":[],"relativePath":"chuong-01-gioi-thieu/muc-01-tong-quan/01-gioi-thieu-cnn-deep-learning.md","filePath":"chuong-01-gioi-thieu/muc-01-tong-quan/01-gioi-thieu-cnn-deep-learning.md","lastUpdated":1766163869000}'),g={name:"chuong-01-gioi-thieu/muc-01-tong-quan/01-gioi-thieu-cnn-deep-learning.md"};function a(o,n,r,e,u,p){return i(),t("div",null,[...n[0]||(n[0]=[c('<h1 id="chuong-1-gioi-thieu-ve-convolutional-neural-network-va-deep-learning-trong-vien-tham" tabindex="-1">Chương 1: Giới thiệu về Convolutional Neural Network và Deep Learning trong Viễn thám <a class="header-anchor" href="#chuong-1-gioi-thieu-ve-convolutional-neural-network-va-deep-learning-trong-vien-tham" aria-label="Permalink to &quot;Chương 1: Giới thiệu về Convolutional Neural Network và Deep Learning trong Viễn thám&quot;">​</a></h1><h2 id="_1-1-boi-canh-va-tam-quan-trong" tabindex="-1">1.1. Bối cảnh và Tầm quan trọng <a class="header-anchor" href="#_1-1-boi-canh-va-tam-quan-trong" aria-label="Permalink to &quot;1.1. Bối cảnh và Tầm quan trọng&quot;">​</a></h2><p>Trong những thập kỷ gần đây, sự phát triển vượt bậc của công nghệ vệ tinh quan sát Trái Đất đã mở ra một kỷ nguyên mới trong lĩnh vực viễn thám. Các vệ tinh quan sát như Sentinel-1, Sentinel-2 của Cơ quan Vũ trụ Châu Âu (ESA), WorldView-3 của Maxar Technologies, hay Planet Labs với hệ thống vệ tinh nhỏ quy mô lớn, liên tục thu thập hàng petabyte dữ liệu ảnh mỗi ngày. Khối lượng dữ liệu khổng lồ này đặt ra thách thức lớn trong việc phân tích và trích xuất thông tin hữu ích một cách tự động và hiệu quả.</p><p>Trước đây, các phương pháp xử lý ảnh viễn thám truyền thống chủ yếu dựa vào việc trích xuất đặc trưng thủ công (handcrafted features) kết hợp với các bộ phân loại cổ điển như Support Vector Machine (SVM), Random Forest, hay các thuật toán threshold đơn giản. Tuy nhiên, những phương pháp này bộc lộ nhiều hạn chế khi đối mặt với sự đa dạng và phức tạp của dữ liệu ảnh vệ tinh thực tế. Các đặc trưng được thiết kế thủ công thường khó có thể tổng quát hóa tốt trên nhiều loại cảnh quan, điều kiện thời tiết, hay góc chụp khác nhau.</p><p>Sự ra đời và phát triển mạnh mẽ của Deep Learning, đặc biệt là mạng Convolutional Neural Network (CNN), đã tạo ra bước đột phá quan trọng trong việc giải quyết các bài toán xử lý ảnh viễn thám. Khác với các phương pháp truyền thống, CNN có khả năng tự động học và trích xuất các đặc trưng phân cấp từ dữ liệu thô, từ các đặc trưng cấp thấp như cạnh (edge), góc (corner), đến các đặc trưng cấp cao như hình dạng đối tượng, mối quan hệ không gian. Khả năng học biểu diễn end-to-end này cho phép CNN đạt được hiệu suất vượt trội so với các phương pháp truyền thống trong hầu hết các bài toán xử lý ảnh viễn thám.</p><p>Trong bối cảnh an ninh hàng hải và bảo vệ môi trường biển, hai bài toán quan trọng được đặc biệt quan tâm là phát hiện tàu biển (Ship Detection) và nhận dạng vết dầu loang (Oil Spill Detection). Phát hiện tàu biển đóng vai trò then chốt trong việc giám sát hoạt động hàng hải, phát hiện tàu đánh cá bất hợp pháp (Illegal, Unreported, and Unregulated fishing - IUU), và đảm bảo an ninh vùng biển. Trong khi đó, nhận dạng vết dầu loang là nhiệm vụ thiết yếu để phát hiện sớm các sự cố tràn dầu, hỗ trợ ứng phó kịp thời và giảm thiểu tác động đến môi trường sinh thái biển.</p><h2 id="_1-2-convolutional-neural-network-la-gi" tabindex="-1">1.2. Convolutional Neural Network là gì? <a class="header-anchor" href="#_1-2-convolutional-neural-network-la-gi" aria-label="Permalink to &quot;1.2. Convolutional Neural Network là gì?&quot;">​</a></h2><p>Convolutional Neural Network, thường được viết tắt là CNN hoặc ConvNet, là một lớp kiến trúc mạng nơ-ron sâu (Deep Neural Network) được thiết kế đặc biệt để xử lý dữ liệu có cấu trúc lưới (grid-like topology), điển hình nhất là dữ liệu ảnh hai chiều. Tên gọi &quot;Convolutional&quot; xuất phát từ phép toán tích chập (convolution) được sử dụng làm thao tác xử lý chính trong mạng, thay thế cho phép nhân ma trận thông thường trong ít nhất một lớp của mạng.</p><p>Ý tưởng cốt lõi của CNN được lấy cảm hứng từ cơ chế hoạt động của vỏ não thị giác (visual cortex) trong não động vật có vú. Các nghiên cứu sinh học thần kinh của Hubel và Wiesel vào những năm 1960 đã chỉ ra rằng các tế bào thần kinh trong vỏ não thị giác phản ứng với các kích thích trong vùng thị giác giới hạn, gọi là receptive field. Các receptive field của các tế bào khác nhau chồng chéo lên nhau một phần để bao phủ toàn bộ trường thị giác. CNN mô phỏng cơ chế này bằng cách sử dụng các bộ lọc (filter hay kernel) có kích thước nhỏ quét qua toàn bộ ảnh đầu vào.</p><p>Điểm khác biệt quan trọng giữa CNN và mạng nơ-ron truyền thống fully connected là ba tính chất kiến trúc đặc trưng: kết nối cục bộ (local connectivity), chia sẻ trọng số (weight sharing), và pooling. Trong mạng fully connected, mỗi nơ-ron ở lớp sau được kết nối với tất cả các nơ-ron ở lớp trước, dẫn đến số lượng tham số rất lớn và dễ bị overfitting. Ngược lại, trong CNN, mỗi nơ-ron chỉ kết nối với một vùng nhỏ của lớp trước (receptive field cục bộ), và các trọng số kết nối được chia sẻ trên toàn bộ ảnh. Điều này không chỉ giảm đáng kể số lượng tham số mà còn cho phép mạng học được các đặc trưng bất biến với vị trí (translation invariant).</p><h2 id="_1-3-lich-su-phat-trien-cua-cnn" tabindex="-1">1.3. Lịch sử Phát triển của CNN <a class="header-anchor" href="#_1-3-lich-su-phat-trien-cua-cnn" aria-label="Permalink to &quot;1.3. Lịch sử Phát triển của CNN&quot;">​</a></h2><h3 id="_1-3-1-giai-đoan-khoi-đau-lenet-1989-1998" tabindex="-1">1.3.1. Giai đoạn Khởi đầu: LeNet (1989-1998) <a class="header-anchor" href="#_1-3-1-giai-đoan-khoi-đau-lenet-1989-1998" aria-label="Permalink to &quot;1.3.1. Giai đoạn Khởi đầu: LeNet (1989-1998)&quot;">​</a></h3><p>Lịch sử của CNN bắt đầu từ những năm cuối thập niên 1980 với công trình tiên phong của Yann LeCun và cộng sự tại AT&amp;T Bell Labs. Năm 1989, LeCun lần đầu tiên áp dụng thành công thuật toán backpropagation để huấn luyện mạng nơ-ron tích chập nhận dạng chữ số viết tay. Đến năm 1998, kiến trúc LeNet-5 được công bố, trở thành một trong những CNN hoàn chỉnh đầu tiên được triển khai trong thực tế để đọc mã ZIP trên thư tín tại Hoa Kỳ.</p><p>LeNet-5 có kiến trúc gồm 7 lớp (không kể đầu vào): 3 lớp convolution, 2 lớp subsampling (tương tự pooling), và 2 lớp fully connected. Mặc dù quy mô nhỏ theo tiêu chuẩn hiện đại, LeNet-5 đã thiết lập nền tảng kiến trúc cơ bản cho các CNN sau này: xen kẽ giữa các lớp convolution và pooling, sau đó kết nối với các lớp fully connected để thực hiện phân loại cuối cùng.</p><h3 id="_1-3-2-thoi-ky-tram-lang-1998-2012" tabindex="-1">1.3.2. Thời kỳ Trầm lắng (1998-2012) <a class="header-anchor" href="#_1-3-2-thoi-ky-tram-lang-1998-2012" aria-label="Permalink to &quot;1.3.2. Thời kỳ Trầm lắng (1998-2012)&quot;">​</a></h3><p>Sau thành công ban đầu của LeNet, nghiên cứu về mạng nơ-ron nói chung và CNN nói riêng trải qua giai đoạn trầm lắng kéo dài gần 15 năm. Nguyên nhân chính bao gồm: hạn chế về năng lực tính toán của phần cứng thời bấy giờ, thiếu các tập dữ liệu quy mô lớn để huấn luyện, và sự thống trị của các phương pháp học máy khác như SVM và boosting vốn cho kết quả tốt với chi phí tính toán thấp hơn.</p><p>Trong giai đoạn này, các phương pháp dựa trên đặc trưng thủ công như SIFT (Scale-Invariant Feature Transform), HOG (Histogram of Oriented Gradients), và các biến thể của chúng chiếm ưu thế trong các bài toán thị giác máy tính. Đối với xử lý ảnh viễn thám, các kỹ thuật truyền thống như phân loại dựa trên chỉ số phổ, phân tích kết cấu (texture analysis), và phân đoạn dựa trên ngưỡng vẫn là những phương pháp chính được sử dụng.</p><h3 id="_1-3-3-buoc-ngoat-alexnet-2012" tabindex="-1">1.3.3. Bước Ngoặt AlexNet (2012) <a class="header-anchor" href="#_1-3-3-buoc-ngoat-alexnet-2012" aria-label="Permalink to &quot;1.3.3. Bước Ngoặt AlexNet (2012)&quot;">​</a></h3><p>Năm 2012 đánh dấu bước ngoặt lịch sử của Deep Learning khi Alex Krizhevsky, Ilya Sutskever và Geoffrey Hinton giành chiến thắng áp đảo trong cuộc thi ImageNet Large Scale Visual Recognition Challenge (ILSVRC). Mạng AlexNet của họ đạt top-5 error rate 15.3%, vượt xa đáng kể so với phương pháp xếp thứ hai với 26.2%. Khoảng cách vượt trội này đã gây chấn động cộng đồng nghiên cứu và khởi đầu kỷ nguyên Deep Learning hiện đại.</p><p>AlexNet có kiến trúc sâu hơn đáng kể so với LeNet với 8 lớp học được (5 lớp convolution và 3 lớp fully connected), sử dụng hàm kích hoạt ReLU thay cho sigmoid/tanh, áp dụng kỹ thuật Dropout để chống overfitting, và được huấn luyện trên GPU - một bước tiến quan trọng về mặt kỹ thuật. Thành công của AlexNet chứng minh rằng với đủ dữ liệu và năng lực tính toán, các mạng nơ-ron sâu có thể học được các biểu diễn đặc trưng mạnh mẽ hơn nhiều so với các đặc trưng thiết kế thủ công.</p><h3 id="_1-3-4-ky-nguyen-mang-rat-sau-vggnet-va-resnet-2014-2015" tabindex="-1">1.3.4. Kỷ nguyên Mạng Rất Sâu: VGGNet và ResNet (2014-2015) <a class="header-anchor" href="#_1-3-4-ky-nguyen-mang-rat-sau-vggnet-va-resnet-2014-2015" aria-label="Permalink to &quot;1.3.4. Kỷ nguyên Mạng Rất Sâu: VGGNet và ResNet (2014-2015)&quot;">​</a></h3><p>Sau AlexNet, xu hướng chính trong nghiên cứu CNN là tăng độ sâu của mạng. Năm 2014, nhóm Visual Geometry Group tại Đại học Oxford giới thiệu VGGNet với kiến trúc sử dụng các bộ lọc convolution nhỏ 3×3 xếp chồng lên nhau. VGG-16 và VGG-19 với 16 và 19 lớp học được đạt kết quả ấn tượng trên ImageNet, đồng thời chứng minh rằng độ sâu của mạng là yếu tố quan trọng quyết định hiệu suất.</p><p>Tuy nhiên, việc tăng độ sâu mạng gặp phải vấn đề nghiêm trọng: gradient vanishing và gradient exploding trong quá trình backpropagation, khiến việc huấn luyện các mạng rất sâu trở nên khó khăn hoặc không thể. Năm 2015, Kaiming He và cộng sự tại Microsoft Research đề xuất kiến trúc Residual Network (ResNet) với ý tưởng đột phá: thêm các kết nối tắt (skip connection hay shortcut connection) cho phép gradient chảy trực tiếp qua nhiều lớp. Với kỹ thuật này, ResNet có thể huấn luyện thành công các mạng với hàng trăm thậm chí hàng nghìn lớp. ResNet-152 đạt top-5 error rate 3.57% trên ImageNet, lần đầu tiên vượt qua ngưỡng nhận dạng của con người (khoảng 5%).</p><h3 id="_1-3-5-tu-efficientnet-đen-vision-transformer-2019-hien-tai" tabindex="-1">1.3.5. Từ EfficientNet đến Vision Transformer (2019-Hiện tại) <a class="header-anchor" href="#_1-3-5-tu-efficientnet-đen-vision-transformer-2019-hien-tai" aria-label="Permalink to &quot;1.3.5. Từ EfficientNet đến Vision Transformer (2019-Hiện tại)&quot;">​</a></h3><p>Năm 2019, Mingxing Tan và Quoc V. Le từ Google Research giới thiệu EfficientNet với phương pháp compound scaling - cân bằng đồng thời ba chiều: độ sâu, độ rộng, và độ phân giải của mạng. EfficientNet đạt được độ chính xác state-of-the-art với số lượng tham số và chi phí tính toán ít hơn đáng kể so với các kiến trúc trước đó.</p><p>Bước ngoặt mới nhất trong lĩnh vực thị giác máy tính là sự xuất hiện của Vision Transformer (ViT) vào năm 2020. Lấy cảm hứng từ kiến trúc Transformer vốn thành công vượt trội trong xử lý ngôn ngữ tự nhiên, ViT chia ảnh thành các patch và xử lý như một chuỗi token. Khi được huấn luyện trên tập dữ liệu đủ lớn, ViT đạt được hoặc vượt qua hiệu suất của các CNN tốt nhất trên nhiều benchmark. Các biến thể như Swin Transformer còn kết hợp ưu điểm của cả CNN (xử lý phân cấp) và Transformer (attention toàn cục).</p><h2 id="_1-4-tai-sao-cnn-phu-hop-voi-anh-ve-tinh" tabindex="-1">1.4. Tại sao CNN phù hợp với ảnh vệ tinh? <a class="header-anchor" href="#_1-4-tai-sao-cnn-phu-hop-voi-anh-ve-tinh" aria-label="Permalink to &quot;1.4. Tại sao CNN phù hợp với ảnh vệ tinh?&quot;">​</a></h2><p>Ảnh vệ tinh có nhiều đặc điểm khác biệt so với ảnh tự nhiên thông thường, và CNN tỏ ra đặc biệt phù hợp để xử lý những đặc thù này.</p><p>Thứ nhất, ảnh vệ tinh thường có kích thước rất lớn, từ hàng nghìn đến hàng chục nghìn pixel mỗi chiều. Một scene Sentinel-1 điển hình có kích thước khoảng 250×250 km với độ phân giải 10m, tương đương ảnh 25,000×25,000 pixel. Kiến trúc CNN với các lớp convolution và pooling cho phép xử lý ảnh lớn một cách hiệu quả thông qua việc chia nhỏ thành các tile và trượt cửa sổ.</p><p>Thứ hai, các đối tượng trong ảnh vệ tinh xuất hiện ở nhiều tỷ lệ khác nhau. Một tàu container có thể chiếm hàng trăm pixel trong ảnh WorldView-3 (0.3m GSD) nhưng chỉ vài pixel trong ảnh Sentinel-1 (10m GSD). Các kiến trúc CNN hiện đại như Feature Pyramid Network (FPN) được thiết kế đặc biệt để xử lý vấn đề đa tỷ lệ này bằng cách kết hợp các đặc trưng từ nhiều mức độ phân giải khác nhau.</p><p>Thứ ba, ảnh vệ tinh thường có nhiều kênh phổ hơn ảnh RGB thông thường. Ảnh Sentinel-2 có 13 kênh phổ từ khả kiến đến hồng ngoại nhiệt, trong khi ảnh Sentinel-1 SAR có 2 kênh phân cực VV và VH. CNN có thể dễ dàng mở rộng để xử lý đầu vào đa kênh bằng cách điều chỉnh số kênh ở lớp convolution đầu tiên.</p><p>Thứ tư, các đối tượng trong ảnh vệ tinh có thể xuất hiện theo nhiều hướng khác nhau, không như ảnh tự nhiên thường có hướng &quot;lên-xuống&quot; cố định. Tính chất translation invariant của CNN, kết hợp với data augmentation xoay ảnh trong quá trình huấn luyện, giúp mạng có khả năng nhận dạng đối tượng bất kể hướng.</p><h2 id="_1-5-cac-lop-bai-toan-chinh-trong-ung-dung-deep-learning-cho-vien-tham" tabindex="-1">1.5. Các lớp bài toán chính trong ứng dụng Deep Learning cho viễn thám <a class="header-anchor" href="#_1-5-cac-lop-bai-toan-chinh-trong-ung-dung-deep-learning-cho-vien-tham" aria-label="Permalink to &quot;1.5. Các lớp bài toán chính trong ứng dụng Deep Learning cho viễn thám&quot;">​</a></h2><p>Khi áp dụng Deep Learning vào phân tích ảnh viễn thám, các nhà nghiên cứu và kỹ sư thường đối mặt với bốn lớp bài toán cốt lõi: phân loại hình ảnh, phân đoạn ngữ nghĩa, phát hiện đối tượng, và giám sát thay đổi. Mỗi lớp bài toán phục vụ một mục đích riêng biệt và mở ra những hướng ứng dụng đa dạng trong nhiều lĩnh vực từ nông nghiệp, đô thị hóa, đến quản lý thiên tai và bảo vệ môi trường. Hiểu rõ bản chất và tiềm năng của từng lớp bài toán sẽ giúp định hướng việc lựa chọn phương pháp phù hợp cho các nhiệm vụ cụ thể.</p><h3 id="_1-5-1-phan-loai-hinh-anh-image-classification" tabindex="-1">1.5.1. Phân loại hình ảnh (Image Classification) <a class="header-anchor" href="#_1-5-1-phan-loai-hinh-anh-image-classification" aria-label="Permalink to &quot;1.5.1. Phân loại hình ảnh (Image Classification)&quot;">​</a></h3><p>Phân loại hình ảnh là lớp bài toán nền tảng nhất trong Deep Learning cho viễn thám. Bài toán này nhằm gán một hoặc nhiều nhãn mô tả cho toàn bộ ảnh hoặc một vùng ảnh (patch), trả lời câu hỏi đơn giản nhưng quan trọng: &quot;Đây là loại cảnh quan gì?&quot; hay &quot;Vùng này thuộc lớp phủ nào?&quot;</p><p>Trong lĩnh vực viễn thám, phân loại hình ảnh được ứng dụng rộng rãi trong việc nhận dạng và phân loại các loại hình sử dụng đất. Một hệ thống phân loại có thể xác định một vùng ảnh là rừng nguyên sinh, đất nông nghiệp, khu công nghiệp, hay vùng đô thị dựa trên các đặc trưng phổ và không gian được học từ dữ liệu. Điều này hỗ trợ đắc lực cho công tác quy hoạch đô thị, quản lý tài nguyên thiên nhiên và đánh giá tác động môi trường.</p><p>Phân loại cảnh quan (scene classification) là một hướng ứng dụng quan trọng khác, trong đó hệ thống nhận dạng toàn cảnh một khu vực như cảng biển, sân bay, khu dân cư, hay vùng nông thôn. Khả năng phân loại cảnh quan quy mô lớn cho phép tự động hóa việc lập bản đồ sử dụng đất ở quy mô quốc gia hoặc toàn cầu, phục vụ cho các mục tiêu phát triển bền vững và giám sát biến đổi khí hậu.</p><p>Trong lĩnh vực hàng hải, phân loại hình ảnh được áp dụng để nhận dạng loại tàu biển từ ảnh vệ tinh. Hệ thống có thể phân biệt giữa tàu chở hàng container, tàu dầu, tàu đánh cá, hay tàu quân sự dựa trên hình dạng và kích thước. Thông tin này hỗ trợ cho việc giám sát hoạt động hàng hải, phát hiện các hành vi đáng ngờ, và quản lý giao thông đường biển.</p><p>Một ưu điểm lớn của phân loại hình ảnh là tính đơn giản trong việc chuẩn bị dữ liệu huấn luyện. Người dùng chỉ cần gán nhãn cho toàn bộ ảnh hoặc patch thay vì phải đánh dấu chi tiết từng pixel hay vẽ bounding box. Điều này giúp giảm đáng kể chi phí annotation và cho phép xây dựng các tập dữ liệu quy mô lớn trong thời gian ngắn.</p><h3 id="_1-5-2-phan-đoan-ngu-nghia-semantic-segmentation" tabindex="-1">1.5.2. Phân đoạn ngữ nghĩa (Semantic Segmentation) <a class="header-anchor" href="#_1-5-2-phan-đoan-ngu-nghia-semantic-segmentation" aria-label="Permalink to &quot;1.5.2. Phân đoạn ngữ nghĩa (Semantic Segmentation)&quot;">​</a></h3><p>Phân đoạn ngữ nghĩa đưa việc phân tích ảnh lên một tầm chi tiết cao hơn bằng cách gán nhãn cho từng pixel trong ảnh. Thay vì chỉ xác định &quot;có rừng trong ảnh này&quot;, phân đoạn ngữ nghĩa trả lời câu hỏi &quot;chính xác những pixel nào thuộc về rừng?&quot; Kết quả là một bản đồ phân vùng có độ phân giải ngang bằng với ảnh đầu vào, mô tả chi tiết ranh giới giữa các lớp đối tượng khác nhau.</p><p>Trong viễn thám, phân đoạn ngữ nghĩa là công cụ không thể thiếu cho việc lập bản đồ lớp phủ mặt đất chi tiết. Các ứng dụng bao gồm phân vùng diện tích rừng để giám sát phá rừng, trích xuất hệ thống sông ngòi để quản lý tài nguyên nước, hay khoanh vùng đất nông nghiệp để dự báo sản lượng mùa vụ. Độ chính xác ở mức pixel cho phép đo đạc diện tích với sai số nhỏ, hỗ trợ các quyết định quản lý dựa trên số liệu định lượng.</p><p>Nhận dạng vết dầu loang trên biển là một ứng dụng tiêu biểu của phân đoạn ngữ nghĩa trong bảo vệ môi trường biển. Hệ thống phân tích ảnh radar SAR để phân biệt các vùng nước bị ô nhiễm dầu với bề mặt biển bình thường, hỗ trợ việc phát hiện sớm các sự cố tràn dầu và điều phối hoạt động ứng phó. Khả năng khoanh vùng chính xác diện tích bị ảnh hưởng giúp đánh giá mức độ nghiêm trọng của sự cố và ước tính khối lượng dầu tràn.</p><p>Lập bản đồ ngập lụt là một ứng dụng quan trọng khác trong quản lý thiên tai. Khi xảy ra lũ lụt, ảnh vệ tinh được chụp và phân đoạn nhanh chóng để xác định vùng bị ngập, hỗ trợ công tác cứu hộ và đánh giá thiệt hại. Việc so sánh với dữ liệu trước đó còn cho phép đánh giá xu hướng ngập lụt theo thời gian, phục vụ quy hoạch phòng chống thiên tai dài hạn.</p><p>Trích xuất vùng đô thị và công trình xây dựng là một hướng ứng dụng đang phát triển mạnh. Phân đoạn ngữ nghĩa cho phép tự động tạo bản đồ nhà cửa, đường sá, và cơ sở hạ tầng từ ảnh vệ tinh độ phân giải cao, hỗ trợ quy hoạch đô thị và quản lý tài sản. Các thành phố thông minh có thể sử dụng thông tin này để giám sát quá trình đô thị hóa và phát hiện xây dựng trái phép.</p><h3 id="_1-5-3-phat-hien-đoi-tuong-object-detection" tabindex="-1">1.5.3. Phát hiện đối tượng (Object Detection) <a class="header-anchor" href="#_1-5-3-phat-hien-đoi-tuong-object-detection" aria-label="Permalink to &quot;1.5.3. Phát hiện đối tượng (Object Detection)&quot;">​</a></h3><p>Phát hiện đối tượng tập trung vào việc định vị và nhận dạng các đối tượng riêng lẻ trong ảnh. Khác với phân loại (chỉ cho biết có hay không) và phân đoạn (khoanh vùng toàn bộ lớp), phát hiện đối tượng xác định chính xác vị trí của từng đối tượng thông qua bounding box và gán nhãn phân loại cho nó. Điều này trả lời câu hỏi &quot;Có những đối tượng nào, ở đâu, và thuộc loại gì?&quot;</p><p>Trong lĩnh vực giám sát hàng hải, phát hiện tàu biển từ ảnh vệ tinh là một ứng dụng có tầm quan trọng chiến lược. Hệ thống có thể quét các vùng biển rộng lớn để xác định vị trí của hàng nghìn tàu, hỗ trợ giám sát hoạt động đánh bắt cá, phát hiện tàu đánh cá bất hợp pháp (IUU fishing), và đảm bảo an ninh vùng biển. Với ảnh SAR có khả năng hoạt động ngày đêm và trong mọi điều kiện thời tiết, hệ thống giám sát có thể duy trì hoạt động liên tục.</p><p>Phát hiện phương tiện trên đất liền mở ra nhiều ứng dụng trong quản lý giao thông và quy hoạch đô thị. Hệ thống có thể đếm số lượng xe trong bãi đỗ, ước tính mật độ giao thông trên các tuyến đường chính, hay giám sát hoạt động tại các khu vực quan trọng. Thông tin này hữu ích cho việc lập kế hoạch giao thông, đánh giá nhu cầu cơ sở hạ tầng, và nghiên cứu đô thị.</p><p>Giám sát sân bay và cảng biển là lĩnh vực ứng dụng quan trọng trong an ninh quốc phòng và thương mại. Hệ thống có thể phát hiện và phân loại máy bay tại các sân bay, container tại các cảng, hay các phương tiện quân sự tại căn cứ. Việc theo dõi sự thay đổi số lượng và vị trí theo thời gian cung cấp thông tin giá trị về hoạt động logistics và chiến lược.</p><p>Trong nông nghiệp, phát hiện đối tượng được áp dụng để đếm số lượng cây trồng, xác định vị trí các hố nước, hay nhận dạng các công trình nông nghiệp. Thông tin này hỗ trợ quản lý nông trại quy mô lớn, ước tính sản lượng, và lập kế hoạch tưới tiêu tự động.</p><h3 id="_1-5-4-giam-sat-thay-đoi-change-detection" tabindex="-1">1.5.4. Giám sát thay đổi (Change Detection) <a class="header-anchor" href="#_1-5-4-giam-sat-thay-đoi-change-detection" aria-label="Permalink to &quot;1.5.4. Giám sát thay đổi (Change Detection)&quot;">​</a></h3><p>Giám sát thay đổi là lớp bài toán đặc thù của viễn thám, tận dụng ưu thế của ảnh vệ tinh trong việc quan sát lặp lại cùng một khu vực qua thời gian. Bài toán này so sánh ảnh chụp tại các thời điểm khác nhau để phát hiện những thay đổi đã xảy ra trên bề mặt Trái Đất, trả lời câu hỏi &quot;Điều gì đã thay đổi giữa hai thời điểm và thay đổi ở đâu?&quot;</p><p>Giám sát phá rừng là một trong những ứng dụng quan trọng nhất của change detection. Bằng cách so sánh ảnh vệ tinh qua các tháng hoặc năm, hệ thống có thể tự động phát hiện các vùng rừng bị chặt phá, cháy rừng, hay suy thoái. Thông tin này hỗ trợ các cơ quan quản lý rừng trong việc giám sát và bảo vệ tài nguyên rừng, đồng thời đóng góp vào nỗ lực toàn cầu về bảo tồn đa dạng sinh học và chống biến đổi khí hậu.</p><p>Đánh giá thiệt hại sau thảm họa thiên nhiên là ứng dụng mang tính nhân đạo cao. Khi xảy ra động đất, sóng thần, hay bão lũ, ảnh vệ tinh trước và sau sự kiện được so sánh để xác định các công trình bị hư hại hoặc phá hủy. Thông tin này hỗ trợ công tác cứu trợ, ưu tiên các vùng cần hỗ trợ khẩn cấp, và lập kế hoạch tái thiết. Cuộc thi xView2 về đánh giá thiệt hại công trình sau thảm họa là một ví dụ điển hình về việc ứng dụng Deep Learning cho bài toán này.</p><p>Giám sát quá trình đô thị hóa cho phép theo dõi sự mở rộng của các thành phố qua thời gian. Hệ thống phát hiện các khu vực có xây dựng mới, các vùng đất nông nghiệp bị chuyển đổi mục đích sử dụng, hay sự phát triển của cơ sở hạ tầng. Thông tin này có giá trị cho quy hoạch đô thị, đánh giá tác động môi trường, và nghiên cứu về phát triển bền vững.</p><p>Trong lĩnh vực nông nghiệp, giám sát thay đổi được áp dụng để theo dõi sự phát triển của cây trồng qua các giai đoạn sinh trưởng, phát hiện các vùng bị sâu bệnh, hay đánh giá hiệu quả tưới tiêu. So sánh ảnh đa thời gian giúp nông dân và các cơ quan quản lý nông nghiệp có cái nhìn toàn diện về tình trạng mùa vụ và đưa ra các quyết định kịp thời.</p><p>Giám sát băng tan và biến đổi đường bờ biển là các ứng dụng quan trọng trong nghiên cứu biến đổi khí hậu. Việc so sánh ảnh vệ tinh qua nhiều năm cho phép đo lường sự thu hẹp của các sông băng, sự thay đổi diện tích băng biển Bắc Cực, hay sự xói mòn bờ biển. Những quan sát này cung cấp bằng chứng khoa học về tác động của biến đổi khí hậu và hỗ trợ việc xây dựng các mô hình dự báo.</p><h3 id="_1-5-5-moi-quan-he-va-su-bo-tro-giua-cac-lop-bai-toan" tabindex="-1">1.5.5. Mối quan hệ và sự bổ trợ giữa các lớp bài toán <a class="header-anchor" href="#_1-5-5-moi-quan-he-va-su-bo-tro-giua-cac-lop-bai-toan" aria-label="Permalink to &quot;1.5.5. Mối quan hệ và sự bổ trợ giữa các lớp bài toán&quot;">​</a></h3><p>Bốn lớp bài toán trên không tồn tại độc lập mà có mối quan hệ bổ trợ lẫn nhau trong các ứng dụng thực tế. Phân loại hình ảnh thường được sử dụng như bước tiền xử lý để lọc và phân loại ảnh đầu vào trước khi áp dụng các phương pháp chi tiết hơn. Phát hiện đối tượng có thể kết hợp với phân đoạn để không chỉ định vị mà còn khoanh vùng chính xác từng đối tượng (instance segmentation). Giám sát thay đổi thường tận dụng kết quả phân đoạn tại các thời điểm khác nhau để so sánh và phát hiện biến động.</p><p>Trong nhiều hệ thống giám sát thực tế, các lớp bài toán được kết hợp theo quy trình xử lý phân cấp. Ví dụ, một hệ thống giám sát hàng hải có thể bắt đầu bằng việc phân loại cảnh (loại bỏ ảnh đất liền), sau đó phát hiện tàu (xác định vị trí), phân đoạn (khoanh vùng chính xác thân tàu), và cuối cùng là phân loại loại tàu. Sự kết hợp này cho phép xây dựng các hệ thống phân tích toàn diện, từ cái nhìn tổng thể đến chi tiết cụ thể.</p><p>Việc hiểu rõ bản chất và phạm vi ứng dụng của từng lớp bài toán là nền tảng quan trọng để lựa chọn phương pháp phù hợp cho các nhiệm vụ cụ thể, đồng thời định hướng cho việc nghiên cứu và phát triển các giải pháp Deep Learning trong lĩnh vực viễn thám.</p><h2 id="_1-6-muc-tieu-va-pham-vi-bao-cao" tabindex="-1">1.6. Mục tiêu và Phạm vi Báo cáo <a class="header-anchor" href="#_1-6-muc-tieu-va-pham-vi-bao-cao" aria-label="Permalink to &quot;1.6. Mục tiêu và Phạm vi Báo cáo&quot;">​</a></h2><p>Báo cáo này tập trung vào hai bài toán ứng dụng cụ thể trong lĩnh vực giám sát biển: <strong>phát hiện tàu biển (Ship Detection)</strong> và <strong>nhận dạng vết dầu loang (Oil Spill Detection)</strong>. Đây là hai bài toán có tầm quan trọng chiến lược trong bối cảnh Việt Nam là quốc gia biển với đường bờ biển dài hơn 3,260 km và vùng đặc quyền kinh tế rộng lớn.</p><p>Phát hiện tàu biển từ ảnh vệ tinh là công cụ quan trọng để giám sát hoạt động đánh bắt cá bất hợp pháp, đảm bảo an ninh hàng hải, và quản lý giao thông đường biển. Với việc sử dụng ảnh SAR (Synthetic Aperture Radar) từ vệ tinh Sentinel-1, hệ thống có thể hoạt động liên tục 24/7, bất kể điều kiện thời tiết hay thời gian trong ngày - một ưu điểm vượt trội so với ảnh quang học.</p><p>Nhận dạng vết dầu loang đóng vai trò quan trọng trong bảo vệ môi trường biển. Việc phát hiện sớm các sự cố tràn dầu, dù từ tai nạn hay xả thải bất hợp pháp, cho phép các cơ quan chức năng ứng phó kịp thời, giảm thiểu tác động đến hệ sinh thái biển và ngành thủy sản. Ảnh SAR cũng là lựa chọn hàng đầu cho bài toán này do khả năng phát hiện vết dầu dựa trên sự thay đổi độ nhám bề mặt biển.</p><p>Báo cáo sẽ trình bày chi tiết về kiến trúc CNN cơ bản, các phương pháp áp dụng CNN cho ảnh vệ tinh, quy trình xử lý hoàn chỉnh cho từng bài toán, các model state-of-the-art, và các bộ dữ liệu chuẩn được sử dụng trong nghiên cứu và đánh giá. Đặc biệt, báo cáo sẽ tập trung phân tích các model có sẵn trong thư viện TorchGeo - một thư viện Python được thiết kế đặc biệt cho các bài toán Deep Learning trong viễn thám, với các pre-trained weights cho nhiều loại cảm biến vệ tinh khác nhau.</p><p>Thông qua việc tổng hợp và phân tích các nghiên cứu, phương pháp, và công cụ hiện đại, báo cáo nhằm cung cấp một cái nhìn toàn diện về ứng dụng Deep Learning trong viễn thám biển, đồng thời đề xuất các hướng tiếp cận phù hợp cho việc triển khai thực tế tại Việt Nam.</p>',69)])])}const m=h(g,[["render",a]]);export{v as __pageData,m as default};
