import{_ as c,C as e,c as g,o as n,a2 as i,b as o,w as h,a as r,G as u,a3 as l}from"./chunks/framework.nRfFlDZQ.js";const k=JSON.parse('{"title":"Chương 4: Chuỗi Cuộc Thi xView - Đẩy Mạnh Giới Hạn Thị Giác Máy Tính Trong Viễn Thám","description":"","frontmatter":{},"headers":[],"relativePath":"chuong-04-xview-challenges/00-gioi-thieu-xview.md","filePath":"chuong-04-xview-challenges/00-gioi-thieu-xview.md","lastUpdated":1766302189000}'),s={name:"chuong-04-xview-challenges/00-gioi-thieu-xview.md"};function d(p,t,m,b,x,v){const a=e("Mermaid");return n(),g("div",null,[t[1]||(t[1]=i('<h1 id="chuong-4-chuoi-cuoc-thi-xview-đay-manh-gioi-han-thi-giac-may-tinh-trong-vien-tham" tabindex="-1">Chương 4: Chuỗi Cuộc Thi xView - Đẩy Mạnh Giới Hạn Thị Giác Máy Tính Trong Viễn Thám <a class="header-anchor" href="#chuong-4-chuoi-cuoc-thi-xview-đay-manh-gioi-han-thi-giac-may-tinh-trong-vien-tham" aria-label="Permalink to &quot;Chương 4: Chuỗi Cuộc Thi xView - Đẩy Mạnh Giới Hạn Thị Giác Máy Tính Trong Viễn Thám&quot;">​</a></h1><h2 id="gioi-thieu" tabindex="-1">Giới thiệu <a class="header-anchor" href="#gioi-thieu" aria-label="Permalink to &quot;Giới thiệu&quot;">​</a></h2><p>Các kiến trúc mô hình đã giới thiệu ở <strong>Chương 3</strong> được kiểm chứng qua loạt cuộc thi xView do Defense Innovation Unit tổ chức. Chương này phân tích 3 cuộc thi và 15 giải pháp chiến thắng, minh họa cách áp dụng các kỹ thuật deep learning vào bài toán viễn thám thực tế.</p><h2 id="loi-mo-đau" tabindex="-1">Lời Mở Đầu <a class="header-anchor" href="#loi-mo-đau" aria-label="Permalink to &quot;Lời Mở Đầu&quot;">​</a></h2><p>Trong lịch sử phát triển của thị giác máy tính, những cuộc thi benchmark lớn như ImageNet Challenge đã đóng vai trò then chốt trong việc thúc đẩy các đột phá kỹ thuật. Tương tự, lĩnh vực phân tích ảnh viễn thám cũng chứng kiến sự ra đời của chuỗi cuộc thi xView - một sáng kiến mang tính bước ngoặt do Defense Innovation Unit (DIU) thuộc Bộ Quốc phòng Hoa Kỳ khởi xướng từ năm 2018.</p><p>Chuỗi cuộc thi xView được thiết kế không chỉ nhằm tạo ra các bộ dữ liệu benchmark chất lượng cao, mà còn hướng tới việc giải quyết những bài toán thực tiễn có ý nghĩa chiến lược: từ phát hiện và phân loại hàng chục nghìn đối tượng trên mặt đất, đánh giá thiệt hại công trình sau thảm họa, đến giám sát hoạt động hàng hải và chống đánh bắt cá bất hợp pháp. Mỗi thử thách đều đặt ra những yêu cầu kỹ thuật đặc thù, buộc cộng đồng nghiên cứu phải phát triển các phương pháp mới phù hợp với đặc điểm riêng của ảnh viễn thám.</p><h2 id="tong-quan-chuoi-cuoc-thi" tabindex="-1">Tổng Quan Chuỗi Cuộc Thi <a class="header-anchor" href="#tong-quan-chuoi-cuoc-thi" aria-label="Permalink to &quot;Tổng Quan Chuỗi Cuộc Thi&quot;">​</a></h2><p>Chuỗi xView Challenges bao gồm ba cuộc thi chính, mỗi cuộc thi tập trung vào một bài toán cốt lõi khác nhau trong lĩnh vực thị giác máy tính cho viễn thám:</p>',8)),(n(),o(l,null,{default:h(()=>[u(a,{id:"mermaid-24",class:"mermaid",graph:"timeline%0A%20%20%20%20title%20D%C3%B2ng%20Th%E1%BB%9Di%20Gian%20Chu%E1%BB%97i%20Cu%E1%BB%99c%20Thi%20xView%0A%20%20%20%202018%20%3A%20xView1%20-%20Object%20Detection%20Challenge%0A%20%20%20%20%20%20%20%20%20%3A%20Dataset%20~1%20tri%E1%BB%87u%20bounding%20boxes%0A%20%20%20%20%20%20%20%20%20%3A%2060%20l%E1%BB%9Bp%20%C4%91%E1%BB%91i%20t%C6%B0%E1%BB%A3ng%20%C4%91a%20d%E1%BA%A1ng%0A%20%20%20%202019%20%3A%20xView2%20-%20Building%20Damage%20Assessment%0A%20%20%20%20%20%20%20%20%20%3A%20Dataset%20xBD%20v%E1%BB%9Bi%20850%2C000%2B%20c%C3%B4ng%20tr%C3%ACnh%0A%20%20%20%20%20%20%20%20%20%3A%20%C4%90%C3%A1nh%20gi%C3%A1%20thi%E1%BB%87t%20h%E1%BA%A1i%20sau%20th%E1%BA%A3m%20h%E1%BB%8Da%0A%20%20%20%202021-2022%20%3A%20xView3%20-%20Maritime%20Detection%0A%20%20%20%20%20%20%20%20%20%20%20%20%20%20%3A%20Dataset%20SAR%20l%E1%BB%9Bn%20nh%E1%BA%A5t%20th%E1%BA%BF%20gi%E1%BB%9Bi%0A%20%20%20%20%20%20%20%20%20%20%20%20%20%20%3A%20Ph%C3%A1t%20hi%E1%BB%87n%20t%C3%A0u%20%22t%E1%BB%91i%22%20v%C3%A0%20ch%E1%BB%91ng%20IUU%20fishing%0A"})]),fallback:h(()=>[...t[0]||(t[0]=[r(" Loading... ",-1)])]),_:1})),t[2]||(t[2]=i('<h3 id="xview1-nen-tang-phat-hien-đoi-tuong-quy-mo-lon" tabindex="-1">xView1: Nền Tảng Phát Hiện Đối Tượng Quy Mô Lớn <a class="header-anchor" href="#xview1-nen-tang-phat-hien-đoi-tuong-quy-mo-lon" aria-label="Permalink to &quot;xView1: Nền Tảng Phát Hiện Đối Tượng Quy Mô Lớn&quot;">​</a></h3><p>Cuộc thi xView1 (2018) đánh dấu sự khởi đầu của chuỗi với mục tiêu xây dựng một benchmark toàn diện cho bài toán phát hiện đối tượng trong ảnh vệ tinh quang học độ phân giải cao. Với gần một triệu bounding box annotation trải rộng trên 60 lớp đối tượng - từ phương tiện giao thông, công trình xây dựng đến các cơ sở hạ tầng - xView1 đặt ra những thách thức chưa từng có về quy mô và độ phức tạp. Các giải pháp hàng đầu từ cuộc thi này đã đặt nền móng cho nhiều kỹ thuật quan trọng được áp dụng rộng rãi trong phân tích ảnh viễn thám sau này.</p><h3 id="xview2-đanh-gia-thiet-hai-cong-trinh" tabindex="-1">xView2: Đánh Giá Thiệt Hại Công Trình <a class="header-anchor" href="#xview2-đanh-gia-thiet-hai-cong-trinh" aria-label="Permalink to &quot;xView2: Đánh Giá Thiệt Hại Công Trình&quot;">​</a></h3><p>Tiếp nối thành công của xView1, cuộc thi xView2 (2019) chuyển hướng sang một ứng dụng mang tính nhân đạo sâu sắc: đánh giá thiệt hại công trình sau các thảm họa tự nhiên và nhân tạo. Bộ dữ liệu xBD (xView Building Damage) được giới thiệu như dataset đánh giá thiệt hại công trình lớn nhất từng được công bố, với hơn 850,000 polygon công trình được gán nhãn theo bốn cấp độ thiệt hại. Cuộc thi không chỉ đòi hỏi khả năng định vị công trình mà còn yêu cầu phân tích sự thay đổi giữa các cặp ảnh trước và sau thảm họa - một bài toán đa nhiệm phức tạp đòi hỏi sự kết hợp tinh tế giữa phát hiện đối tượng và phân tích thay đổi.</p><h3 id="xview3-giam-sat-hang-hai-va-chong-đanh-bat-ca-bat-hop-phap" tabindex="-1">xView3: Giám Sát Hàng Hải và Chống Đánh Bắt Cá Bất Hợp Pháp <a class="header-anchor" href="#xview3-giam-sat-hang-hai-va-chong-đanh-bat-ca-bat-hop-phap" aria-label="Permalink to &quot;xView3: Giám Sát Hàng Hải và Chống Đánh Bắt Cá Bất Hợp Pháp&quot;">​</a></h3><p>Cuộc thi xView3 (2021-2022) đánh dấu một bước chuyển đổi đáng kể khi chuyển từ ảnh quang học sang ảnh radar khẩu độ tổng hợp (SAR - Synthetic Aperture Radar). Được tổ chức hợp tác giữa DIU và Global Fishing Watch, xView3 hướng tới mục tiêu phát hiện &quot;dark vessels&quot; - những tàu cố tình tắt hệ thống nhận dạng tự động (AIS) để trốn tránh giám sát. Dataset xView3-SAR với 1,400 gigapixels dữ liệu trở thành bộ dữ liệu SAR phát hiện tàu lớn nhất thế giới, mở ra một hướng nghiên cứu mới trong việc ứng dụng deep learning cho dữ liệu radar vệ tinh.</p><h2 id="so-sanh-ba-cuoc-thi" tabindex="-1">So Sánh Ba Cuộc Thi <a class="header-anchor" href="#so-sanh-ba-cuoc-thi" aria-label="Permalink to &quot;So Sánh Ba Cuộc Thi&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Tiêu chí</th><th>xView1 (2018)</th><th>xView2 (2019)</th><th>xView3 (2021-2022)</th></tr></thead><tbody><tr><td><strong>Bài toán</strong></td><td>Object Detection</td><td>Building Damage Assessment</td><td>Maritime Detection</td></tr><tr><td><strong>Loại ảnh</strong></td><td>Quang học (RGB)</td><td>Quang học (RGB)</td><td>SAR (Radar)</td></tr><tr><td><strong>Vệ tinh</strong></td><td>WorldView-3</td><td>Maxar satellites</td><td>Sentinel-1</td></tr><tr><td><strong>GSD</strong></td><td>0.3m</td><td>0.3-0.8m</td><td>10m × 10m</td></tr><tr><td><strong>Đối tượng</strong></td><td>~1M bounding boxes</td><td>~850K polygons</td><td>~10K vessels</td></tr><tr><td><strong>Số lớp</strong></td><td>60 lớp</td><td>5 lớp (4 damage + background)</td><td>Binary + length</td></tr><tr><td><strong>Diện tích</strong></td><td>~1,400 km²</td><td>~45,000 km²</td><td>43M km² ocean</td></tr><tr><td><strong>Task</strong></td><td>Single-stage detection</td><td>Multi-task (localization + damage)</td><td>Detection + classification</td></tr><tr><td><strong>Thách thức chính</strong></td><td>Class imbalance, multi-scale</td><td>Change detection, disaster diversity</td><td>Dark vessels, SAR noise</td></tr><tr><td><strong>Metric</strong></td><td>mAP@0.5</td><td>F1 (localization + damage)</td><td>F1 score</td></tr><tr><td><strong>Top score</strong></td><td>31.74% mAP</td><td>0.804 F1</td><td>0.694 F1</td></tr></tbody></table><h2 id="cac-mau-hinh-chien-thang-chung" tabindex="-1">Các Mẫu Hình Chiến Thắng Chung <a class="header-anchor" href="#cac-mau-hinh-chien-thang-chung" aria-label="Permalink to &quot;Các Mẫu Hình Chiến Thắng Chung&quot;">​</a></h2><p>Phân tích 15 giải pháp hàng đầu từ 3 cuộc thi cho thấy các mẫu hình kỹ thuật được lặp lại:</p><h3 id="_1-kien-truc-backbone" tabindex="-1">1. Kiến Trúc Backbone <a class="header-anchor" href="#_1-kien-truc-backbone" aria-label="Permalink to &quot;1. Kiến Trúc Backbone&quot;">​</a></h3><ul><li><strong>xView1</strong>: ResNet, ResNeXt, SENet đa dạng</li><li><strong>xView2</strong>: Chủ yếu EfficientNet, DenseNet</li><li><strong>xView3</strong>: ResNet, EfficientNet cho SAR</li><li><strong>Xu hướng</strong>: Chuyển sang kiến trúc hiệu quả hơn theo thời gian</li></ul><h3 id="_2-ky-thuat-ensemble" tabindex="-1">2. Kỹ Thuật Ensemble <a class="header-anchor" href="#_2-ky-thuat-ensemble" aria-label="Permalink to &quot;2. Kỹ Thuật Ensemble&quot;">​</a></h3><ul><li><strong>Tất cả 3 cuộc thi</strong>: Top 5 đều dùng ensemble 5-15 models</li><li><strong>Chiến lược</strong>: Test-time augmentation + multi-scale inference</li><li><strong>Trọng số</strong>: Voting có trọng số dựa trên validation performance</li></ul><h3 id="_3-xu-ly-mat-can-bang-du-lieu" tabindex="-1">3. Xử Lý Mất Cân Bằng Dữ Liệu <a class="header-anchor" href="#_3-xu-ly-mat-can-bang-du-lieu" aria-label="Permalink to &quot;3. Xử Lý Mất Cân Bằng Dữ Liệu&quot;">​</a></h3><ul><li><strong>xView1</strong>: Reduced Focal Loss, class-balanced sampling</li><li><strong>xView2</strong>: Focal Loss cho damage classes</li><li><strong>xView3</strong>: Weighted loss cho vessel detection</li><li><strong>Bài học</strong>: Loss function engineering quan trọng hơn architecture</li></ul><h3 id="_4-data-augmentation" tabindex="-1">4. Data Augmentation <a class="header-anchor" href="#_4-data-augmentation" aria-label="Permalink to &quot;4. Data Augmentation&quot;">​</a></h3><ul><li><strong>xView1/2</strong>: Flip, rotate, color jitter, mosaic</li><li><strong>xView3</strong>: SAR-specific: speckle noise, intensity normalization</li><li><strong>Copy-paste</strong>: Hiệu quả cho rare classes (xView1, xView3)</li></ul><h3 id="_5-post-processing" tabindex="-1">5. Post-processing <a class="header-anchor" href="#_5-post-processing" aria-label="Permalink to &quot;5. Post-processing&quot;">​</a></h3><ul><li><strong>xView1</strong>: Soft-NMS, score thresholding per class</li><li><strong>xView2</strong>: Morphological operations, polygon refinement</li><li><strong>xView3</strong>: Confidence calibration, spatial filtering</li></ul><h2 id="tam-quan-trong-khoa-hoc-va-ung-dung-thuc-tien" tabindex="-1">Tầm Quan Trọng Khoa Học và Ứng Dụng Thực Tiễn <a class="header-anchor" href="#tam-quan-trong-khoa-hoc-va-ung-dung-thuc-tien" aria-label="Permalink to &quot;Tầm Quan Trọng Khoa Học và Ứng Dụng Thực Tiễn&quot;">​</a></h2><p>Chuỗi cuộc thi xView mang lại những đóng góp quan trọng cho cả cộng đồng nghiên cứu và các ứng dụng thực tiễn:</p><p><strong>Về mặt khoa học</strong>, các cuộc thi đã thúc đẩy sự phát triển của nhiều kỹ thuật đột phá:</p><ul><li>Các hàm mất mát chuyên biệt như Reduced Focal Loss được thiết kế riêng cho bài toán mất cân bằng lớp cực độ trong ảnh viễn thám</li><li>Các kiến trúc mạng kết hợp như Siamese-UNet cho bài toán phân tích thay đổi</li><li>Các phương pháp xử lý ảnh SAR và kết hợp dữ liệu đa nguồn</li><li>Các chiến lược huấn luyện tối ưu cho dữ liệu quy mô lớn với nhãn nhiễu</li></ul><p><strong>Về mặt ứng dụng</strong>, các giải pháp từ chuỗi xView đã được triển khai vào thực tế:</p><ul><li>Hỗ trợ các tổ chức cứu trợ nhân đạo trong việc đánh giá nhanh thiệt hại sau thảm họa</li><li>Tích hợp vào hệ thống giám sát hàng hải của Global Fishing Watch để chống đánh bắt cá bất hợp pháp</li><li>Cung cấp nền tảng cho các nghiên cứu về quy hoạch đô thị và giám sát môi trường</li></ul><h2 id="cau-truc-chuong" tabindex="-1">Cấu Trúc Chương <a class="header-anchor" href="#cau-truc-chuong" aria-label="Permalink to &quot;Cấu Trúc Chương&quot;">​</a></h2><p>Chương này sẽ trình bày chi tiết về cả ba cuộc thi trong chuỗi xView, tập trung vào ba khía cạnh chính cho mỗi cuộc thi:</p><ol><li><p><strong>Bộ dữ liệu và bài toán</strong>: Phân tích đặc điểm kỹ thuật của dataset, các thách thức đặc thù, và định nghĩa chính xác của bài toán cần giải quyết</p></li><li><p><strong>Các giải pháp hàng đầu</strong>: Trình bày chi tiết về phương pháp của các đội đạt thành tích cao, tập trung vào những đổi mới kỹ thuật cốt lõi và các bài học rút ra</p></li><li><p><strong>Tác động và hướng phát triển</strong>: Đánh giá ảnh hưởng của cuộc thi đối với lĩnh vực nghiên cứu và các ứng dụng thực tiễn</p></li></ol><p>Thông qua việc phân tích sâu các giải pháp hàng đầu, chương này không chỉ cung cấp kiến thức về các kỹ thuật tiên tiến mà còn giúp người đọc hiểu được quá trình tư duy và các quyết định thiết kế quan trọng trong việc giải quyết các bài toán phức tạp của thị giác máy tính trong viễn thám.</p><hr><h2 id="ket-chuong" tabindex="-1">Kết Chương <a class="header-anchor" href="#ket-chuong" aria-label="Permalink to &quot;Kết Chương&quot;">​</a></h2><p>Các kỹ thuật và bài học từ xView Challenges sẽ được áp dụng cụ thể vào hai bài toán thực tế: <strong>Chương 5</strong> trình bày phát hiện tàu biển (liên quan đến xView1/xView3), và <strong>Chương 6</strong> trình bày phát hiện dầu loang (ứng dụng kỹ thuật segmentation tương tự xView2).</p><hr><p><em>Các mục tiếp theo sẽ đi sâu vào từng cuộc thi, bắt đầu với xView1 - Object Detection Challenge, cuộc thi đặt nền móng cho toàn bộ chuỗi.</em></p>',35))])}const T=c(s,[["render",d]]);export{k as __pageData,T as default};
